mapper.select:
SELECT COUNT(1)
  FROM GNR
WHERE GNR_NM = #{_parameter}

mapper.update:
	UPDATE MOVIE.GNR 
	   SET GNR_NM=#{gnrNm}
	     , MDFY_DT= SYSDATE
	     , MDFYR=#{mdfyr}
	     , USE_YN=NVL(#{useYn},'N')
     WHERE GNR_ID=#{gnrId}

	UPDATE MOVIE.GNR 
	   SET DEL_YN='Y'
     WHERE GNR_ID IN #{_parameter}
